import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class CreateHabit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      name: "",
      duration: 0,
      datesCompleted: new Date(),
      users: [],
    };
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDuration = this.onChangeDuration.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
  }

  componentDidMount() {
    this.setState({
      users: ["test user", "test user other"],
      username: "test-username",
    });
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }

  onChangeDuration(e) {
    this.setState({
      duration: e.target.value,
    });
  }

  onChangeDate(date) {
    this.setState({
      date: date,
    });
  }

  onSubmit(e) {
    e.preventDefault();
    const habit = {
      name: this.state.name,
      duration: this.state.duration,
      datesCompleted: this.state.datesCompleted,
    };
    console.log(habit);
  }

  render() {
    return (
      <div className="container">
        <form onSubmit={this.onSubmit}>
          <label>Username</label>
          <select
            ref="userInput"
            required
            className="form-control"
            value={this.state.username}
            onChange={this.onChangeUsername}
          >
            {this.state.users.map((user) => {
              return (
                <option key={user} value={user}>
                  {user}
                </option>
              );
            })}
          </select>
          <input
            type="text"
            value={this.state.name}
            onChange={this.onChangeName}
            placeholder="Name of habit"
          />
          <input
            type="text"
            value={this.state.duration}
            onChange={this.onChangeDuration}
            placeholder="Duration of habit in minutes"
          />
          <label>Date</label>
          <DatePicker
            selected={this.state.datesCompleted}
            onChange={this.onChangeDate}
          />
          <input type="Submit" value="Submit" />
        </form>
      </div>
    );
  }
}
