import React, { useEffect } from "react";
import { Link } from "react-router-dom";

const Habit = (props) => {
  useEffect(() => {
    checkHabit(props.habit);
  }, []);

  // use effect update streak :)
  return (
    <tr>
      <td>{props.habit.name}</td>
      <td>{props.habit.duration}</td>
      <td>{props.habit.streak}</td>
      <td>{props.habit.longestStreak}</td>
      <td>{props.habit.percentCompliance}</td>

      <td>
        <Link to={"/edit/" + props.habit._id}>EDIT</Link> |{" "}
        <button
          onClick={() => {
            console.log(props.habit._id);
            props.deleteHabit(props.habit._id);
          }}
        >
          delete
        </button>
        <button onClick={() => props.updateHabitStatus(props.habit)}>
          {props.notCheckedYet(props.habit) ? "mark" : "unmark"}
        </button>
      </td>
    </tr>
  );
};

export default Habit;
