import React, { useEffect, useState } from "react";
import axios from "axios";

const Habit = (props) => {
  const [isEditing, setEditing] = useState(false);
  const [updatedHabit, setUpdatedHabit] = useState("");
  const [duration, setDuration] = useState(0);

  const habit = props.habit;
  const datesCompleted = props.habit.datesCompleted;

  useEffect(() => {
    //Checks whether the habit has been completed or not
    if (!datesCompleted.length) {
      return;
    }
    props.checkHabit(props.habit);
  }, [datesCompleted]);

  const handleClick = () => {
    if (!isEditing) {
      setHabit(props.habit.name);
      setEditing(!isEditing);
      return;
    }
    const updatedHabit = {
      name: habit,
      duration: duration,
      datesCompleted: props.habit.datesCompleted,
      startDate: props.habit.startDate,
    };
    axios
      .post(
        "http://localhost:5000/habits/update/" + props.habit._id,
        updatedHabit
      )
      .then((res) => {
        props.updateHabitsList(res.data);
        setEditing(!isEditing);
      });
  };

  return (
    <tr>
      {isEditing ? (
        <>
          <input
            type="text"
            value={habit}
            onChange={(e) => setHabit(e.target.value)}
          />

          <input
            type="text"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
          <button onClick={handleClick}>Save</button>
        </>
      ) : (
        <>
          <td>{props.habit.name}</td>
          <td>{props.habit.duration}</td>
          <td>{props.habit.streak}</td>
          <td>{props.habit.longestStreak}</td>
          <td>{props.habit.percentCompliance}%</td>

          <td>
            <button onClick={handleClick}>Edit</button>
            <button
              onClick={() => {
                props.deleteHabit(props.habit._id);
              }}
            >
              Delete
            </button>
            <input
              type="checkbox"
              onClick={() => {
                props.updateHabitStatus(props.habit);
              }}
              checked={!props.notCheckedYet(props.habit)}
            />
          </td>
        </>
      )}
    </tr>
  );
};

export default Habit;
