import React, { Component } from "react";
import axios from "axios";
import Habit from "../components/Habit.component";

const HabitsList = (props) => {
  function deleteHabit(id) {
    axios.delete("http://localhost:5000/habits/" + id).then((res) => {
      console.log(res.data);
      props.setHabits(props.habits.filter((habit) => habit._id !== id));
    });
  }

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <td>Name</td>
            <td>Date</td>
            <td>Duration</td>
            <td>Actions</td>
          </tr>
        </thead>
        <tbody>
          {props.habits.map((habit) => (
            <Habit habit={habit} deleteHabit={deleteHabit} key={habit._id} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HabitsList;
