import React, { useState, useEffect } from "react";
import axios from "axios";
import CreateHabit from "./create-habit.component";
import HabitsList from "./habits-list.component";
import Button from "./button.component";

export default function Dashboard(props) {
  const [habits, setHabits] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isAdding, setIsAdding] = useState(false);

  function handleLogoutClick() {
    axios
      .get("http://localhost:5000/logout", { withCredentials: true })
      .then((response) => {
        props.handleLogout();
      })
      .catch((error) => {
        console.log("logout error", error);
      });
  }

  useEffect(() => {
    axios
      .get("http://localhost:5000/user", { withCredentials: true })
      .then((res) => console.log(res));
    axios
      .get("http://localhost:5000/habits", { withCredentials: true })
      .then((res) => {
        setHabits(res.data);
        setIsLoading(false);
      })
      .catch((err) => console.log(err));
  }, []);

  const changeHandler = (habit) => {
    setHabits((prevHabitArray) => [...prevHabitArray, habit]);
  };

  const updateHabit = (habit) => {
    setHabits(
      habits.map((existingHabit) =>
        existingHabit._id === habit._id ? habit : existingHabit
      )
    );
  };

  const deleteHandler = (newArray) => {
    setHabits(newArray);
  };

  const handleClick = (val) => {
    setIsAdding(val);
  };

  if (isLoading) {
    return <p>Page is loading</p>;
  }

  return (
    <div>
      <h1>Welcome {props.user}!</h1>
      <button onClick={handleLogoutClick}>Logout</button>
      {isLoading ? <p> Please wait for habits to load! </p> : null}
      {habits.length ? (
        <HabitsList
          habits={habits}
          setHabits={deleteHandler}
          updateHabit={updateHabit}
          isAdding={isAdding}
        />
      ) : (
        <p>You haven't started adding habits yet!</p>
      )}
      {isAdding ? (
        <CreateHabit
          username={props.user}
          habits={habits}
          setHabits={changeHandler}
          setIsAdding={handleClick}
        />
      ) : (
        <Button handleClick={handleClick} />
      )}
    </div>
  );
}
