import React, { Component } from "react";
import axios from "axios";

export default class HabitsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      habits: [],
    };
    this.deleteHabit = this.deleteHabit.bind(this);
  }

  deleteHabit(id) {
    axios
      .delete("http://localhost:3000/habits/" + id)
      .then((res) => console.log(res.data));
    this.setState({
      habits: this.state.habits.filter((habit) => habit._id !== id),
    });
  }

  habitsList() {
    return this.state.habits.map((habit) => (
      <Habit habit={habit} deleteHabit={this.deleteHabit} key={habit._id} />
    ));
  }

  componentDidMount() {
    axios
      .get("http://localhost:3000/habits/")
      .then((res) => this.setState({ habits: res.data }))
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <div className="container">
        <table>
          <thead>
            <th>Username</th>
            <th>Name</th>
            <th>Date</th>
            <th>Duration</th>
          </thead>
          <tbody>{this.habitsList()}</tbody>
        </table>
      </div>
    );
  }
}
