import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from "axios";

import HabitsList from "./components/habits-list.component";
import EditHabit from "./components/edit-habit.component";
import CreateUser from "./components/create-user.component";
import CreateHabit from "./components/create-habit.component";
import Home from "./components/home.component";
import Dashboard from "./components/dashboard.component";
import PrivateRoute from "./components/privateroute.component";

export default function App() {
  const [isLoggedIn, setLogIn] = useState("NOT_LOGGED_IN");
  const [user, setUser] = useState("");

  const handleLogin = (data) => {
    setLogIn("LOGGED_IN");
    setUser(data.user);
  };

  return (
    <Router>
      <Route
        path={"/dashboard"}
        exact
        render={(props) => <Dashboard {...props} loggedInStatus={isLoggedIn} />}
      />
      <Route
        path={"/"}
        exact
        render={(props) => <Home {...props} loggedInStatus={isLoggedIn} />}
      />
    </Router>
  );
}
