import React, { useEffect, useState } from "react";
import axios from "axios";

const Habit = (props) => {
  const [isEditing, setEditing] = useState(false);
  const [updatedHabit, setUpdatedHabit] = useState("");
  const [duration, setDuration] = useState(0);

  const habit = props.habit;
  console.log(habit);

  function notCheckedYet(habit) {
    return (
      habit.datesCompleted.length === 0 ||
      habit.datesCompleted[habit.datesCompleted.length - 1].date
        .toString()
        .substring(0, 10) !== new Date().toISOString().substring(0, 10)
    );
  }
  useEffect(() => {
    //Checks whether the habit has been completed or not
    if (!habit.datesCompleted.length) {
      return;
    }
    props.checkHabit(habit);
  }, []);

  const handleClick = () => {
    if (!isEditing) {
      setUpdatedHabit(habit.name);
      setEditing(!isEditing);
      return;
    }
    const updatedHabit = {
      name: updatedHabit,
      duration: duration,
      datesCompleted: habit.datesCompleted,
      startDate: habit.startDate,
    };
    axios
      .post("http://localhost:5000/habits/update/" + habit._id, updatedHabit)
      .then((res) => {
        props.updateHabitsList(res.data);
        setEditing(!isEditing);
      });
  };

  return (
    <tr>
      {isEditing ? (
        <>
          <input
            type="text"
            value={habit}
            onChange={(e) => setUpdatedHabit(e.target.value)}
          />

          <input
            type="text"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
          <button onClick={handleClick}>Save</button>
        </>
      ) : (
        <>
          <td>{habit.name}</td>
          <td>{habit.duration}</td>
          <td>{habit.streak}</td>
          <td>{habit.longestStreak}</td>
          <td>{habit.percentCompliance}%</td>

          <td>
            <button onClick={handleClick}>Edit</button>
            <button
              onClick={() => {
                props.deleteHabit(props.habit._id);
              }}
            >
              Delete
            </button>
            <input
              type="checkbox"
              onClick={() => {
                props.updateHabitStatus(habit);
              }}
              checked={!props.notCheckedYet(habit)}
            />
          </td>
        </>
      )}
    </tr>
  );
};

export default Habit;
