import React, { useEffect, useState } from "react";
import axios from "axios";

const Habit = (props) => {
  const [isEditing, setEditing] = useState(false);
  const [editedHabit, setEditedHabit] = useState("");
  const [duration, setDuration] = useState(0);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    if (props.habit.datesCompleted.length) {
      props.addUncompletedDates(props.habit);
    }
  }, []);

  const handleClick = () => {
    if (!isEditing) {
      setEditedHabit(props.habit.name);
      setEditing(!isEditing);
      return;
    }
    const updatedHabit = {
      name: editedHabit,
      duration: duration,
      datesCompleted: props.habit.datesCompleted,
      startDate: props.habit.startDate,
    };
    axios
      .post(
        "http://localhost:5000/habits/update/" + props.habit._id,
        updatedHabit
      )
      .then((res) => {
        console.log(res.data);

        props.sendUpdateRequest(res.data);
        setEditing(!isEditing);
      });
  };

  return (
    <div className="habit">
      {isEditing ? (
        <>
          <input
            type="text"
            value={editedHabit}
            onChange={(e) => setEditedHabit(e.target.value)}
          />

          <input
            type="text"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
          <button onClick={handleClick}>Save</button>
        </>
      ) : (
        <>
          <td>{props.habit.name}</td>
          <td>{props.habit.duration}</td>
          {showDetails ? <td>{props.habit.streak}</td> : null}
          {showDetails ? <td>{props.habit.longestStreak}</td> : null}
          {showDetails ? <td>{props.habit.percentCompliance}%</td> : null}

          <td>
            <button onClick={handleClick}>Edit</button>
            <button
              onClick={() => {
                props.deleteHabit(props.habit._id);
              }}
            >
              Delete
            </button>
            <input
              type="checkbox"
              checked={!props.notCheckedYet(props.habit)}
              onClick={() => {
                props.updateHabitStatus(props.habit);
              }}
            />
            <button onClick={() => setShowDetails(!showDetails)}>
              Toggle stats
            </button>
          </td>
        </>
      )}
    </div>
  );
};

export default Habit;
