import React, { useState, useEffect } from "react";
import { UseHistory, BrowserRouter as Router, Route } from "react-router-dom";
import { Redirect } from "react-router";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from "axios";

import Home from "./components/home.component";
import Dashboard from "./components/dashboard.component";
import SecuredRoute from "./components/securedroute.component";

export default function App() {
  let history = useHistory();
  const [isLoggedIn, setLogIn] = useState("NOT_LOGGED_IN");
  const [user, setUser] = useState("");

  useEffect(() => {
    checkLoginStatus();
    console.log(isLoggedIn);
  }, [isLoggedIn]);

  const handleLogin = (data) => {
    setLogIn("LOGGED_IN");
    setUser(data.user);
    history.push("/dashboard");
  };

  const handleLogout = () => {
    setLogIn("NOT_LOGGED_IN");
    setUser({});
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:5000/logout",
    }).then((res) => {});
  };

  const checkLoginStatus = () => {
    axios
      .get("http://localhost:3000/user", { withCredentials: true })
      .then((res) => {
        if (res.data.status === "Logged In") {
          handleLogin(res.data);
        } else if (
          res.data.status === "Logged Out" &&
          isLoggedIn === "LOGGED_IN"
        ) {
          setLogIn("NOT_LOGGED_IN");
          setUser({});
        }
      })
      .catch((err) => console.log(err));
  };

  return (
    <Router>
      <Route
        path={"/"}
        exact
        render={(props) => (
          <Home
            {...props}
            loggedInStatus={isLoggedIn}
            handleLogin={handleLogin}
          />
        )}
      />
      <SecuredRoute
        path={"/dashboard"}
        exact
        loggedInStatus={isLoggedIn}
        handleLogin={handleLogin}
        handleLogout={handleLogout}
        user={user}
        component={Dashboard}
      />
    </Router>
  );
}
