import React, { useState, useEffect } from "react";
import { Route, Redirect } from "react-router";
import axios from "axios";
import UserForm from "./user-form.component";

export default function Home(props) {
  const [isLoading, setIsLoading] = useState(true);
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [registerUsername, setRegisterUsername] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [errors, setErrors] = useState({});

  const handleSuccessfulAuth = (data) => {
    props.handleLogin(data);
    props.history.push("/dashboard");
  };

  const login = () => {
    axios({
      method: "post",
      data: {
        username: loginUsername,
        password: loginPassword,
      },
      withCredentials: true,
      url: "http://localhost:5000/login",
    })
      .then((res) => {
        if (res.data.status === "Successfully Authenticated") {
          handleSuccessfulAuth(res.data);
        }
        if (res.data.status === "Successfully Authenticated") {
          setErrors({
            username:
              "A user with username " + loginUsername + " already exists",
          });
          console.log(errors);
        }
      })
      .catch((err) => console.log("Failed"));
  };

  const register = () => {
    axios({
      method: "post",
      data: {
        username: registerUsername,
        password: registerPassword,
      },
      withCredentials: true,
      url: "http://localhost:5000/register",
    }).then((res) => {
      if (res.data.status === "Successful Registration") {
        axios({
          method: "post",
          data: {
            username: registerUsername,
            password: registerPassword,
          },
          withCredentials: true,
          url: "http://localhost:5000/login",
        })
          .then((res) => {
            if (res.data.status === "Successfully Authenticated") {
              handleSuccessfulAuth(res.data);
            }
          })
          .catch((err) => console.log("Failed"));
      }
    });
  };

  useEffect(() => {
    props.checkLoginStatus();
    if (props.isLoggedIn === "LOGGED_IN") {
      console.log("You are already logged in ");
      props.history.push("/dashboard");
    }
    setIsLoading(false);
  }, []);

  const styles = {
    header: {
      backgroundColor: "#b19cd9",
      alignItems: "center",
    },
  };

  if (isLoading) {
    return <p>Loading</p>;
  } else {
    return (
      <div>
        <div style={styles.header} className="header">
          <h1>Welcome</h1>
          <h2>Begin habit tracking</h2>
        </div>

        <UserForm
          type="Login"
          username={loginUsername}
          password={loginPassword}
          setUsername={setLoginUsername}
          setPassword={setLoginPassword}
          handleClick={login}
        />

        <p> OR </p>

        <UserForm
          type="Register"
          username={registerUsername}
          password={registerPassword}
          setUsername={setRegisterUsername}
          setPassword={setRegisterPassword}
          handleClick={register}
        />
      </div>
    );
  }
}
