import React from "react";
import axios from "axios";
import Habit from "../components/Habit.component";

const HabitsList = (props) => {
  function deleteHabit(id) {
    axios.delete("http://localhost:5000/habits/" + id).then((res) => {
      props.setHabits(props.habits.filter((habit) => habit._id !== id));
    });
  }

  function notCheckedYet(habit) {
    return (
      habit.datesCompleted.length === 0 ||
      habit.datesCompleted[habit.datesCompleted.length - 1].date
        .toString()
        .substring(0, 10) !== new Date().toISOString().substring(0, 10)
    );
  }

  //checks the habit to update
  function checkHabit(habit) {
    const todaysDate = Date.parse(new Date());
    const lastDayCompleted = habit.datesCompleted[
      habit.datesCompleted.length - 1
    ].date.substring(0, 10);
    let dayCounter = 1;
    while (
      new Date(todaysDate - 864e5 * dayCounter).toString().substring(0, 10) !==
      lastDayCompleted.substring(0, 10)
    ) {
      habit.datesCompleted.push({
        date: new Date(todaysDate - 864e5 * dayCounter),
        completed: false,
      });
      dayCounter++;
    }

    axios
      .post("http://localhost:5000/habits/update/" + habit._id, habit)
      .then((res) => {
        props.setHabits(
          props.habits.map((previousHabit) =>
            previousHabit._id === habit._id ? habit : previousHabit
          )
        );
      })
      .catch((err) => console.log(err));
  }

  function updateHabitStatus(habit) {
    if (notCheckedYet(habit)) {
      habit.datesCompleted.push({
        date: new Date(),
        completed: true,
      });
    } else {
      habit.datesCompleted = habit.datesCompleted.slice(0, -1);
    }
    axios
      .post("http://localhost:5000/habits/update/" + habit._id, habit)
      .then((res) => {
        props.updateHabit(res.data);
      })
      .catch((err) => console.log(err));
  }

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <td>Name</td>
            <td>Duration (mins) </td>
            <td>Streak</td>
            <td>Longest Streak</td>
            <td>Compliance </td>
          </tr>
        </thead>
        <tbody>
          {props.habits.map((habit) => (
            <Habit
              habit={habit}
              deleteHabit={deleteHabit}
              updateHabitStatus={updateHabitStatus}
              updateHabitsList={props.updateHabit}
              notCheckedYet={notCheckedYet}
              checkHabit={checkHabit}
              key={habit._id}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HabitsList;
