import React, { Component } from "react";
import axios from "axios";
import Habit from "../components/Habit.component";

const HabitsList = (props) => {
  function deleteHabit(id) {
    axios.delete("http://localhost:5000/habits/" + id).then((res) => {
      props.setHabits(props.habits.filter((habit) => habit._id !== id));
    });
  }

  function updateHabitStatus(habit) {
    //Checks if dates are being repeated
    if (habit.datesCompleted.length >= 1) {
      const date1 = habit.datesCompleted[
        habit.datesCompleted.length - 1
      ].date.substring(0, 10);
      const frog = new Date();
      const date2 = new Date().substring(0, 10);

      if (date1 === date2) {
        habit = habit.datesCompleted.slice(0, -1);
        console.log(habit);
      }
    } else {
      habit.datesCompleted.push({
        date: new Date(),
        completed: true,
      });
    }
    axios
      .post("http://localhost:5000/habits/update/" + habit._id, habit)
      .then((res) => {
        props.updateHabit(res.data);
      })
      .catch((err) => console.log(err));
  }

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <td>Name</td>
            <td>Duration</td>
            <td>Streak</td>
            <td>Longest Streak</td>
            <td>Compliance </td>
            <td>Actions</td>
          </tr>
        </thead>
        <tbody>
          {props.habits.map((habit) => (
            <Habit
              habit={habit}
              deleteHabit={deleteHabit}
              updateHabitStatus={updateHabitStatus}
              key={habit._id}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HabitsList;
