import React, { Component } from "react";
import axios from "axios";
import Habit from "../components/Habit.component";

const HabitsList = (props) => {
  function deleteHabit(id) {
    axios.delete("http://localhost:5000/habits/" + id).then((res) => {
      props.setHabits(props.habits.filter((habit) => habit._id !== id));
    });
  }
  function completeHabit(habit) {
    habit.datesCompleted.push({
      date: new Date(),
      completed: true,
    });
    console.log(habit);
    axios
      .post("http://localhost:5000/habits/update/" + habit._id, habit)
      .then((res) => console.log("updated"))
      .catch((err) => console.log(err));
  }

  if (!props.habits.length && !props.isAdding) {
    return <p>You have no habits yet! add a new one</p>;
  }

  if (props.habits.length) {
    return (
      <div className="container">
        <table>
          <thead>
            <tr>
              <td>Name</td>
              <td>Duration</td>
              <td>Streak</td>
              <td>Longest Streak</td>
              <td>Compliance </td>
              <td>Actions</td>
            </tr>
          </thead>
          <tbody>
            {props.habits.map((habit) => (
              <Habit
                habit={habit}
                deleteHabit={deleteHabit}
                completeHabit={completeHabit}
                key={habit._id}
              />
            ))}
          </tbody>
        </table>
      </div>
    );
  }
};

export default HabitsList;
