import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Habit = (props) => {
  const [isEditing, setEditing] = useState(false);
  const [habit, setHabit] = useState("");
  const [duration, setDuration] = useState(0);

  //what to dow ith the propss
  useEffect(() => {
    if (!props.habit.datesCompleted.length) {
      return;
    }
    props.checkHabit(props.habit);
  }, []);

  const handleClick = () => {
    if (!isEditing) {
      console.log("In edit mode");
      setHabit(props.habit.name);
      setEditing(!isEditing);
      return;
    }
    const updatedHabit = {
      name: habit,
      duration: duration,
      datesCompleted: props.habit.datesCompleted,
      startDate: props.habit.startDate,
    };
    axios
      .post(
        "http://localhost:5000/habits/update/" + props.habit._id,
        updatedHabit
      )
      .then((res) => {
        console.log(res.data);
        updateHabitsList(res.data);
        setEditing(!isEditing);
      });
  };

  // use effect update streak :)
  return (
    <tr>
      {isEditing ? (
        <div>
          <td>
            <input
              type="text"
              value={habit}
              onChange={(e) => setHabit(e.target.value)}
            />
          </td>
          <td>
            <input
              type="text"
              value={duration}
              onChange={(e) => setDuration(e.target.value)}
            />
          </td>
          <button onClick={handleClick}>edit</button>
        </div>
      ) : (
        <div>
          <td>{props.habit.name}</td>
          <td>{props.habit.duration}</td>
          <td>{props.habit.streak}</td>
          <td>{props.habit.longestStreak}</td>
          <td>{props.habit.percentCompliance}%</td>

          <td>
            <button onClick={handleClick}>Edit</button>
            <button
              onClick={() => {
                props.deleteHabit(props.habit._id);
              }}
            >
              Delete
            </button>
            <input
              type="checkbox"
              onClick={() => {
                props.updateHabitStatus(props.habit);
              }}
              checked={!props.notCheckedYet(props.habit)}
            />
          </td>
        </div>
      )}
    </tr>
  );
};

export default Habit;
