import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { Redirect } from "react-router";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from "axios";

import Home from "./components/home.component";
import Dashboard from "./components/dashboard.component";
import SecuredRoute from "./components/securedroute.component";

export default function App() {
  const [isLoggedIn, setLogIn] = useState("NOT_LOGGED_IN");
  const [isLoading, setLoading] = useState(true);
  const [user, setUser] = useState("");

  useEffect(() => {
    checkLoginStatus();
    console.log("status changedd");
  }, [isLoggedIn]);

  function isAuth() {
    return isLoggedIn;
  }
  const handleLogin = (data) => {
    setLogIn("LOGGED_IN");
    setUser(data.user);
  };

  const handleLogout = () => {
    setLogIn("NOT_LOGGED_IN");
    setUser({});
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:5000/logout",
    }).then((res) => {});
  };

  const checkLoginStatus = () => {
    axios
      .get("http://localhost:3000/user", { withCredentials: true })
      .then((res) => {
        if (res.data.status === "Logged In" && isLoggedIn === "NOT_LOGGED_IN") {
          handleLogin(res.data);
        } else if (
          res.data.status === "Logged Out" &&
          isLoggedIn === "LOGGED_IN"
        ) {
          setLogIn("NOT_LOGGED_IN");
          setUser({});
        }
      })
      .catch((err) => console.log(err));
    setLoading(false);
  };

  return (
    <Router>
      {isLoggedIn === "LOGGED_IN" ? (
        <SecuredRoute
          path={"/dashboard"}
          exact
          isAuth={isAuth}
          handleLogin={handleLogin}
          handleLogout={handleLogout}
          user={user}
          component={Dashboard}
        />
      ) : (
        <Route
          path={"/"}
          exact
          render={(props) => (
            <Home
              {...props}
              loggedInStatus={isLoggedIn}
              handleLogin={handleLogin}
            />
          )}
        />
      )}
    </Router>
  );
}
