import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Habit = (props) => {
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    if (!props.habit.datesCompleted.length) {
      return;
    }
    props.checkHabit(props.habit);
  }, []);

  // use effect update streak :)
  return (
    <tr>
      <td>{props.habit.name}</td>
      <td>{props.habit.duration}</td>
      <td>{props.habit.streak}</td>
      <td>{props.habit.longestStreak}</td>
      <td>{props.habit.percentCompliance}%</td>

      <td>
        <button
          onClick={() => {
            setEditing(true);
          }}
        >
          Edit
        </button>
        <button
          onClick={() => {
            props.deleteHabit(props.habit._id);
          }}
        >
          delete
        </button>
        <input
          type="checkbox"
          onClick={() => {
            props.updateHabitStatus(props.habit);
          }}
          checked={!props.notCheckedYet(props.habit)}
        />
      </td>
    </tr>
  );
};

export default Habit;
