import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Habit = (props) => {
  const [isEditing, setEditing] = useState(false);
  const [habit, setHabit] = useState(false);
  const [duration, setDuration] = useState(false);

  useEffect(() => {
    if (!props.habit.datesCompleted.length) {
      return;
    }
    props.checkHabit(props.habit);
  }, []);

  const handleClick = () => {
    if (isEditing) {
      setHabit(props.habit.name);
    }
    setEditing(!isEditing);
    const updatedHabit = {
      name: habit,
      duration: duration,
      datesCompleted: props.habit.datesCompleted,
      startDate: props.habit.startDate,
    };
    axios.post(
      "http://localhost:5000/habits/update/" + props.habit._id,
      updatedHabit
    );
  };

  // use effect update streak :)
  return (
    <tr>
      {isEditing ? (
        <div>
          <td>
            <input type="text" value={habit} onChange={() => setHabit()} />
          </td>
          <td>
            <input type="text" value={duration} onChange={props.updateHabit} />
          </td>
          <button onClick={handleSubmit}>edit</button>
        </div>
      ) : (
        <div>
          <td>{props.habit.name}</td>
          <td>{props.habit.duration}</td>
          <td>{props.habit.streak}</td>
          <td>{props.habit.longestStreak}</td>
          <td>{props.habit.percentCompliance}%</td>

          <td>
            <button
              onClick={() => {
                setEditing(!isEditing);
              }}
            >
              Edit
            </button>
            <button
              onClick={() => {
                props.deleteHabit(props.habit._id);
              }}
            >
              Delete
            </button>
            <input
              type="checkbox"
              onClick={() => {
                props.updateHabitStatus(props.habit);
              }}
              checked={!props.notCheckedYet(props.habit)}
            />
          </td>
        </div>
      )}
    </tr>
  );
};

export default Habit;
