import React, { Component } from "react";
import axios from "axios";
import Habit from "../components/Habit.component";

export default class HabitsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      habits: this.props.habits,
    };
    this.deleteHabit = this.deleteHabit.bind(this);
  }

  deleteHabit(id) {
    console.log("Delete habit called");
    axios
      .delete("http://localhost:5000/habits/" + id)
      .then((res) => console.log(res.data));
    this.setState({
      habits: this.state.habits.filter((habit) => habit._id !== id),
    });
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/habits/")
      .then((res) => {
        this.setState({ habits: res.data });
      })
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <div className="container">
        <table>
          <thead>
            <tr>
              <td>Username</td>
              <td>Name</td>
              <td>Date</td>
              <td>Duration</td>
              <td>Actions</td>
            </tr>
          </thead>
          <tbody>
            {this.state.habits.map((habit) => (
              <Habit
                habit={habit}
                deleteHabit={this.deleteHabit}
                key={habit._id}
              />
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
