import React, { useState, useEffect, Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { Redirect } from "react-router";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from "axios";

import Home from "./components/home.component";
import Dashboard from "./components/dashboard.component";
import SecuredRoute from "./components/securedroute.component";

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      isLoggedIn: "NOT_LOGGED_IN",
      user: "",
    };
    this.checkLoginStatus = this.checkLoginStatus.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.checkLoginStatus = this.checkLoginStatus.bind(this);
  }

  componentDidMount() {
    this.checkLoginStatus();
  }

  componentDidUpdate() {
    this.checkLoginStatus();
  }

  handleLogin(data) {
    this.setState({
      isLoggedIn: "LOGGED_IN",
      user: data.user.username,
    });
  }

  handleLogout = () => {
    axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:5000/logout",
    })
      .then((res) => console.log("Logge out"))
      .catch((err) => "log out failed");
    this.setState({
      isLoggedIn: "NOT_LOGGED_IN",
      user: "",
    });
  };

  checkLoginStatus = () => {
    axios
      .get("http://localhost:5000/user", { withCredentials: true })
      .then((res) => {
        console.log(res);
        if (
          res.data.status === "Logged In" &&
          this.isLoggedIn === "NOT_LOGGED_IN"
        ) {
          this.handleLogin();
        } else if (
          res.data.status === "Logged Out" &&
          this.isLoggedIn === "LOGGED_IN"
        ) {
          this.setState({
            isLoggedIn: "NOT_LOGGED_IN",
            user: "",
          });
        }
      })
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <Router>
        <SecuredRoute
          path={"/dashboard"}
          exact
          loggedInStatus={this.isLoggedIn}
          handleLogin={this.handleLogin}
          handleLogout={this.handleLogout}
          user={this.user}
          component={Dashboard}
        />
        <Route
          path={"/"}
          exact
          render={(props) => (
            <Home
              {...props}
              user={this.user}
              loggedInStatus={this.isLoggedIn}
              handleLogin={this.handleLogin}
            />
          )}
        />
      </Router>
    );
  }
}
