import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from "axios";

export default class CreateHabit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      name: "",
      duration: 0,
      datesCompleted: new Date(),
      users: [],
    };
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDuration = this.onChangeDuration.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/users/")
      .then((response) => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map((user) => user.username),
            username: response.data[0].username,
          });
        }
      })
      .catch((err) => console.log(err));
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }

  onChangeDuration(e) {
    this.setState({
      duration: e.target.value,
    });
  }

  onChangeDate(date) {
    this.setState({
      datesCompleted: date,
    });
  }

  onSubmit(e) {
    e.preventDefault();
    const habit = {
      username: this.state.username,
      name: this.state.name,
      duration: this.state.duration,
      datesCompleted: this.state.datesCompleted,
    };
    console.log(habit);
    axios
      .post("http://localhost:5000/habits/add", habit)
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div className="container">
        <form onSubmit={this.onSubmit}>
          <label>Username</label>
          <select
            ref="userInput"
            required
            className="form-control"
            value={this.state.username}
            onChange={this.onChangeUsername}
          >
            {this.state.users.map((user) => {
              return (
                <option key={user} value={user}>
                  {user}
                </option>
              );
            })}
          </select>
          <input
            type="text"
            value={this.state.name}
            onChange={this.onChangeName}
            placeholder="Name of habit"
          />
          <input
            type="text"
            value={this.state.duration}
            onChange={this.onChangeDuration}
            placeholder="Duration of habit in minutes"
          />
          <label>Date</label>
          <DatePicker
            selected={this.state.datesCompleted}
            onChange={this.onChangeDate}
          />
          <input type="Submit" value="Submit" />
        </form>
      </div>
    );
  }
}
